INSERT INTO `tbl_post` (`id_post`, `title_post`, `content_post`, `image_post`, `id_category_post`) VALUES
(1, 'Anh chàng này đã dành 5 năm để xây dựng 1 thành phố LEGO rộng lớn ', '<p>LEGO l&agrave; 1 thương hiệu đồ chơi xếp h&igrave;nh l&acirc;u đời v&agrave; đ&atilde; gắn b&oacute; với rất nhiều thế hệ trẻ em tr&ecirc;n to&agrave;n thế giới, đến nay vẫn tiếp tục duy tr&igrave; vị thế cực khủng của m&igrave;nh. Sức h&uacute;t của thương hiệu n&agrave;y mạnh đến nỗi ngay cả những người trưởng th&agrave;nh cũng sẵn s&agrave;ng chi ra h&agrave;ng chục triệu đồng để thỏa m&atilde;n đam m&ecirc;, hay đơn giản l&agrave; quay về tuổi thơ của họ. V&iacute; dụ như anh ch&agrave;ng Josh Foote dưới đ&acirc;y, d&ugrave; đ&atilde; 35 tuổi v&agrave; thậm ch&iacute; l&agrave; l&ecirc;n chức cha rồi, vẫn kh&ocirc;ng ngần ngại d&agrave;nh ra 5 năm để x&acirc;y dựng 1 đ&ocirc; thị LEGO cho ri&ecirc;ng m&igrave;nh, với tổng chi ph&iacute; gần 100.000 USD (khoảng hơn 2 tỉ đồng).</p>\r\n\r\n<p>Mọi chuyện bắt đầu khi gia đ&igrave;nh Josh dọn đến 1 căn nh&agrave; mới ở New Jersey v&agrave;o năm 2015, nơi c&oacute; cả 1 tầng hầm rộng lớn với diện t&iacute;ch hơn 90 m&eacute;t vu&ocirc;ng. Vốn c&oacute; niềm đam m&ecirc; m&atilde;nh liệt với tr&ograve; chơi xếp h&igrave;nh, anh quyết định x&acirc;y dựng lu&ocirc;n 1 th&agrave;nh phố LEGO cho ri&ecirc;ng m&igrave;nh, hiện đ&atilde; chiếm khoảng 50% căn ph&ograve;ng n&agrave;y rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Anh chàng này đã dành 5 năm để xây dựng 1 thành phố LEGO rộng lớn trị giá hơn 2 tỉ đồng, có cả tòa tháp Avengers hẳn hoi - Ảnh 1.\" src=\"https://genk.mediacdn.vn/thumb_w/660/139269124445442048/2020/6/2/1-15911067191801941932375.jpg\" /></p>\r\n\r\n<p>&Ocirc;ng bố 1 con nhưng vẫn giữ được niềm đam m&ecirc; m&atilde;nh liệt với c&aacute;c bộ xếp h&igrave;nh LEGO.</p>\r\n\r\n<p>Để thực hiện được điều đ&oacute;, Josh đ&atilde; phải mua tổng cộng 1.600 bộ LEGO kh&aacute;c nhau (khoảng 81.000 miếng xếp h&igrave;nh), chủ yếu l&agrave; m&ocirc; h&igrave;nh c&aacute;c t&ograve;a nh&agrave;, căn hộ v&agrave; phương tiện giao th&ocirc;ng. Gia đ&igrave;nh của anh kh&ocirc;ng phản đối dự &aacute;n n&agrave;y, nhưng dường như cũng kh&ocirc;ng để t&acirc;m cho lắm. Chỉ khi tự m&igrave;nh bước xuống tầng hầm, họ mới nhận ra th&agrave;nh phố của Josh rộng lớn v&agrave; chi tiết đến mức độ n&agrave;o.</p>\r\n\r\n<p>Chỉ t&iacute;nh ri&ecirc;ng phần nền của &ldquo;th&agrave;nh phố&rdquo; n&agrave;y th&ocirc;i đ&atilde; ngốn của Josh khoảng 50.000 USD (hơn 1 tỉ đồng) rồi. C&ograve;n lại thường l&agrave; những bộ nhỏ lẻ như t&ograve;a th&aacute;p Avengers, viện bảo t&agrave;ng hay c&aacute;c t&ograve;a nh&agrave; chọc trời, c&oacute; gi&aacute; dao động từ 500 USD (khoảng 11,6 triệu đồng) đến 1000 USD (khoảng 23 triệu đồng). Theo ước t&iacute;nh của Josh, anh đ&atilde; chi ra khoảng 96.000 USD cho dự &aacute;n của m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Anh chàng này đã dành 5 năm để xây dựng 1 thành phố LEGO rộng lớn trị giá hơn 2 tỉ đồng, có cả tòa tháp Avengers hẳn hoi - Ảnh 2.\" src=\"https://genk.mediacdn.vn/thumb_w/660/139269124445442048/2020/6/2/3-15911067192141710500018.jpg\" /></p>\r\n\r\n<p>Th&agrave;nh phố của Josh chiếm khoảng 50% diện t&iacute;ch tầng hầm, c&oacute; cả t&ograve;a th&aacute;p Avengers hẳn hoi.</p>\r\n\r\n<p>Anh cho biết: &ldquo;<em>LEGO l&agrave; 1 phần kh&ocirc;ng thể thiếu trong qu&aacute; tr&igrave;nh trưởng th&agrave;nh của t&ocirc;i, v&agrave; t&ocirc;i đ&atilde; m&ecirc; mẩn những bộ xếp h&igrave;nh n&agrave;y từ khi c&ograve;n b&eacute; x&iacute;u. T&ocirc;i muốn tạo ra 1 th&agrave;nh phố m&agrave; bất k&igrave; c&ocirc; cậu nh&oacute;c n&agrave;o trong những năm 90 cũng mơ ước được sở hữu với bộ đồ chơi của m&igrave;nh. Cũng giống như nhiều người kh&aacute;c, t&ocirc;i dần qu&ecirc;n đi niềm đam m&ecirc; xếp h&igrave;nh của m&igrave;nh khi lớn l&ecirc;n v&agrave; d&agrave;nh thời gian cho nhiều th&uacute; vui kh&aacute;c, v&iacute; dụ như thể thao hay c&aacute;c hoạt động tại trường học</em>&rdquo;.</p>\r\n', 'lego1604050143.jpg', 4),
(2, 'Thanh niên hì hục cả tuần lắp mô hình 4.500 miếng Lego hình Doraemon', '<h2>Tệ hơn, kh&aacute;ch y&ecirc;u cầu phải giao h&agrave;ng trước năm 2020, phen n&agrave;y toang thật rồi sen ạ.</h2>\r\n\r\n<p>Mới đ&acirc;y, anh ch&agrave;ng c&oacute; nick Facebook Boogy.Jun, chủ một cửa h&agrave;ng đồ chơi ở Th&aacute;i Lan đ&atilde; l&ecirc;n mạng b&oacute;c phốt boss m&egrave;o của m&igrave;nh v&igrave; c&aacute;i tội ph&aacute; tan t&agrave;nh m&ocirc; h&igrave;nh Doraemon m&agrave; anh đ&atilde; d&agrave;y c&ocirc;ng lắp r&aacute;p.</p>\r\n\r\n<p>Lo&agrave;i m&egrave;o vốn nghịch ngợm v&agrave; thường xuy&ecirc;n l&agrave;m rơi, hỏng c&aacute;c đồ vật trong nh&agrave;. Nhưng mọi chuyện đ&acirc;u chỉ đơn giản c&oacute; vậy. Được biết, m&ocirc; h&igrave;nh Doraemon n&agrave;y bao gồm 4.500 miếng xếp h&igrave;nh logo, v&agrave; được anh chủ h&igrave; hục lắp trong suốt 7 ng&agrave;y liền. Họa v&ocirc; đơn ch&iacute;, đ&acirc;y l&agrave; đơn h&agrave;ng cuối năm của kh&aacute;ch đặt, chứ đ&acirc;u phải anh lắp để trưng b&agrave;y cho vui.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Thanh niên hì hục cả tuần lắp mô hình 4.500 miếng Lego hình Doraemon, chưa kịp giao khách thì bị mèo phá tan tành trong 1 nốt nhạc - Ảnh 1.\" src=\"https://genk.mediacdn.vn/thumb_w/660/2020/1/2/1-1577953238903498997440.jpg\" /></p>\r\n\r\n<p>M&ocirc; h&igrave;nh Doraemon gồm 4.500 miếng logo được anh chủ cửa h&agrave;ng đồ chơi cặm cụi lắp suốt 1 tuần mới xong.</p>\r\n', 'loe1604050344.jpg', 4),
(3, 'Ngỡ ngàng với bộ mô hình tí hon siêu thực, trông chẳng khác gì đồ thật', '<h2>mẩn t&ocirc; vẽ suốt 3 năm liền.</h2>\r\n\r\n<p>Nghệ thuật l&agrave; kh&ocirc;ng c&oacute; giới hạn, v&agrave; bộ &oacute;c s&aacute;ng tạo của c&aacute;c nghệ sĩ sẽ lu&ocirc;n khiến ch&uacute;ng ta phải trầm trồ trước những t&aacute;c phẩm tưởng như sẽ kh&ocirc;ng ai c&oacute; thể tạo ra được. Mới đ&acirc;y, họa sĩ người Malaysia Eddie Putera đ&atilde; g&acirc;y sốt tr&ecirc;n cộng đồng Facebook Talenthouse nhờ bộ sưu tập m&ocirc; h&igrave;nh si&ecirc;u thực của m&igrave;nh. Từ những d&atilde;y nh&agrave; cấp 4, những sạp hoa quả ven đường, những chiếc &ocirc; t&ocirc; bon bon chạy, tất cả đều được b&agrave;n tay t&agrave;i hoa của &ocirc;ng t&aacute;i hiện lại một c&aacute;ch ch&acirc;n thật v&agrave; sinh động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Ngỡ ngàng với bộ mô hình tí hon siêu thực, trông chẳng khác gì đồ thật, có cái được bán với giá gần 10 triệu đồng - Ảnh 1.\" src=\"https://genk.mediacdn.vn/thumb_w/660/2019/12/18/1-157665689451829040723.jpg\" /></p>\r\n\r\n<p>Từ h&igrave;nh d&aacute;ng, tỉ lệ, cho đến m&agrave;u sắc, chất liệu, tất cả đều được t&aacute;i tạo một c&aacute;ch ho&agrave;n hảo.</p>\r\n\r\n<p>Ngay cả những chất liệu như tường gạch với r&ecirc;u phong ẩm mốc, m&aacute;i t&ocirc;n bạc m&agrave;u sau nhiều thập kỷ che chắn nắng mưa, cũng được chăm ch&uacute;t tỉ mỉ, kh&ocirc;ng kh&aacute;c ảnh chụp chứ chẳng ai nghĩ l&agrave; người nghệ sĩ 51 tuổi n&agrave;y vẽ cả.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Ngỡ ngàng với bộ mô hình tí hon siêu thực, trông chẳng khác gì đồ thật, có cái được bán với giá gần 10 triệu đồng - Ảnh 2.\" src=\"https://genk.mediacdn.vn/thumb_w/660/2019/12/18/2-15766568945192090036733.jpg\" /></p>\r\n\r\n<p>Độ chi tiết đ&aacute;ng nể trong những m&ocirc; h&igrave;nh của họa sĩ Eddie/</p>\r\n', 'mohinh1604050398.jpg', 3),
(4, 'Eric Siow - Nghệ nhân người Malaysia chuyên làm thức ăn miniature', '<h2>&nbsp;</h2>\r\n\r\n<p>Qua c&aacute;c hoạt động gửi thầy c&ocirc; c&oacute; thể thấy sức s&aacute;ng tạo của học tr&ograve; ng&agrave;y nay l&agrave; v&ocirc; bi&ecirc;n. Nhiều bạn c&ograve;n kh&eacute;o tay &quot;h&ocirc; biến&quot; những chiếc bảng đơn điệu th&agrave;nh thiệp ch&uacute;c mừng cỡ đại theo từng chủ đề muốn nhắn gửi thầy c&ocirc;. Học sinh tha hồ m&uacute;a b&uacute;t tạo n&ecirc;n t&aacute;c phẩm đa sắc m&agrave;u khiến gi&aacute;o vi&ecirc;n kh&ocirc;ng ai nỡ x&oacute;a đi c&aacute;c bức vẽ dễ thương đ&oacute;.</p>\r\n\r\n<p>Mới đ&acirc;y, cuộc thi vẽ b&aacute;o tường tr&ecirc;n bảng của trường THPT Triệu Sơn 1 đang g&acirc;y sốt rần rần cộng đồng mạng. Chỉ trong v&ograve;ng 3 tiếng, c&aacute;c bạn học sinh đ&atilde; tạo n&ecirc;n chiếc bảng đa sắc m&agrave;u từ những &yacute; tưởng độc lạ. Người ta chỉ cần nh&igrave;n v&agrave;o l&agrave; nhận ra ngay lời nhắn dễ thương cho mỗi thầy c&ocirc;. C&aacute;c bạn cũng chỉ vẽ nửa bảng để d&agrave;nh nửa bảng cho thầy c&ocirc; vừa giảng b&agrave;i vừa ngắm lại kh&ocirc;ng đắn đo xem c&oacute; nỡ x&oacute;a kh&ocirc;ng. Đ&uacute;ng l&agrave; một c&ocirc;ng đ&ocirc;i việc đ&uacute;ng kh&ocirc;ng n&agrave;o!</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/11/19/1-0927398-15741296966481325887650.jpg\" target=\"_blank\"><img alt=\"Đẹp không chê vào đâu được với những màn múa phấn trên bảng của học sinh, từng chi tiết xuất sắc như hoạ sĩ vậy - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/620/2019/11/19/1-0927398-15741296966481325887650.jpg\" /></a></p>\r\n', 'han1604050543.png', 3),
(5, 'Điều gì sẽ xảy ra nếu Luffy trở thành một người lính hải quân trong One Piece?', '<h2>Garp lu&ocirc;n muốn đứa ch&aacute;u Luffy gia nhập Hải qu&acirc;n, nhưng ở l&agrave;ng Foosha, Luffy đ&atilde; gặp Shanks v&agrave; quyết định sẽ trở th&agrave;nh hải tặc.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Khi Luffy c&ograve;n nhỏ, Garp đ&atilde; mang đứa ch&aacute;u Luffy đến để Dadan chăm s&oacute;c, ở đ&oacute; Luffy đ&atilde; gặp Sabo v&agrave; Ace, c&ograve;n c&oacute; quyết định trở th&agrave;nh hải tặc.&nbsp;Cuối c&ugrave;ng khi Luffy lớn l&ecirc;n, đ&atilde; qu&aacute; muộn để Garp thay đổi quyết t&acirc;m của ch&aacute;u m&igrave;nh.</p>\r\n\r\n<p>Nhưng nếu ở 1 diễn biến kh&aacute;c, Luffy&nbsp;trở th&agrave;nh Thủy qu&acirc;n lục chiến theo đ&uacute;ng &yacute; nguyện của &ocirc;ng nội th&igrave; sao?</p>\r\n\r\n<p><strong>1. Sẽ l&agrave; 1 Luffy ho&agrave;n to&agrave;n kh&aacute;c</strong></p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-1-1603958801921549650091.jpg\" target=\"_blank\"><img alt=\"Điều gì sẽ xảy ra nếu Luffy trở thành một người lính hải quân trong One Piece? - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/photo-1-1603958801921549650091.jpg\" /></a></p>\r\n\r\n<p>Nghĩ lại th&igrave; mọi thứ ch&uacute;ng ta biết về Luffy b&acirc;y giờ đều bắt nguồn từ một người, đ&oacute; l&agrave; Shanks. Shanks đ&atilde; đưa Mũ Rơm cho Luffy. Tr&aacute;i Gomu Gomu no Mi m&agrave; Luffy v&ocirc; t&igrave;nh ăn phải cũng do băng Shanks kiếm được. Shanks. Bản chất của việc l&agrave;m cướp biển l&agrave; tự do v&agrave; vui vẻ của Luffy cũng được lấy cảm hứng từ nh&oacute;m của Shanks.</p>\r\n\r\n<p>Thử nghĩ m&agrave; xem, nếu Luffy ở trong Hải qu&acirc;n th&igrave; c&oacute; lẽ cậu sẽ kh&ocirc;ng gặp Shanks.&nbsp;C&oacute; thể l&agrave; Garp đang quan s&aacute;t ch&aacute;u m&igrave;nh cẩn thận hơn v&agrave; tự m&igrave;nh huấn luyện Luffy. Hoặc Garp sẽ để Luffy được hướng dẫn bởi một hậu bối m&agrave; &ocirc;ng coi trọng, chẳng hạn như Aokiji hoặc Smoker.&nbsp;Nếu đ&uacute;ng như vậy th&igrave; Luffy kh&ocirc;ng sở hữu Mũ Rơm, cũng như kh&ocirc;ng c&oacute; năng lực của tr&aacute;i&nbsp;Gomu Gomu no Mi.</p>\r\n\r\n<p>Cũng tức l&agrave;, thay v&igrave; chiến đấu bằng c&aacute;ch sử dụng năng lực cao su của&nbsp;Gomu Gomu no Mi,&nbsp;c&aacute;ch chiến đấu của Luffy c&oacute; khả năng sẽ tận dụng lợi thế của&nbsp;Rokushiki&nbsp;v&agrave;&nbsp;Haki.</p>\r\n\r\n<p>Koby v&agrave; Helmeppo c&oacute; thể sử dụng một số kỹ thuật&nbsp;Rokushiki&nbsp;khi họ được huấn luyện.&nbsp;Nếu Luffy trở th&agrave;nh một Thủy qu&acirc;n lục chiến, cậu ấy c&oacute; thể th&agrave;nh thạo kỹ thuật n&agrave;y rất tốt nhờ t&agrave;i năng chiến đấu phi thường của m&igrave;nh.&nbsp;Về&nbsp;Haki, Luffy đ&atilde; c&oacute; t&agrave;i năng về Haoshoku Haki&nbsp;ngay từ đầu.&nbsp;</p>\r\n\r\n<p><strong>2. Luffy c&oacute; thể kh&ocirc;ng gặp băng Mũ Rơm</strong></p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-1-1603958805471899370860.jpg\" target=\"_blank\"><img alt=\"Điều gì sẽ xảy ra nếu Luffy trở thành một người lính hải quân trong One Piece? - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/photo-1-1603958805471899370860.jpg\" /></a></p>\r\n\r\n<p>V&acirc;ng, điều 1 sẽ ảnh hưởng tới hướng ph&aacute;t triển của Luffy.&nbsp;Con đường phi&ecirc;u lưu của Luffy sẽ ho&agrave;n to&agrave;n kh&aacute;c nếu trở th&agrave;nh 1 l&iacute;nh hải qu&acirc;n.&nbsp;Anh ta sẽ phục vụ ở Loguetown với Smoker hoặc tham gia c&ugrave;ng với Garp, như Koby v&agrave; Helmeppo.&nbsp;V&igrave; vậy, khả năng Luffy gặp được những người đồng đội trong băng Mũ Rơm l&agrave; điều kh&oacute; xảy ra.&nbsp;Những nh&acirc;n vật như Zoro, Sanji, Nami v&agrave; Usopp c&oacute; thể kh&ocirc;ng gi&uacute;p được anh ta.</p>\r\n\r\n<p>Th&agrave;nh vi&ecirc;n băng Mũ Rơm m&agrave; Luffy gặp nhiều nhất c&oacute; thể l&agrave; Nico Robin, nếu Hải qu&acirc;n quyết định điều tra c&aacute;c hoạt động của Crocodile ở Alabasta.&nbsp;</p>\r\n\r\n<p><strong>3. Luffy vẫn sẽ nổi loạn, giống như Garp</strong></p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-2-16039588069191496185754.jpg\" target=\"_blank\"><img alt=\"Điều gì sẽ xảy ra nếu Luffy trở thành một người lính hải quân trong One Piece? - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/photo-2-16039588069191496185754.jpg\" /></a></p>\r\n\r\n<p>V&acirc;ng, mặc d&ugrave; Luffy l&agrave; một Thủy qu&acirc;n lục chiến, nhưng bản chất của người sở hữu&nbsp;chữ D. trong t&ecirc;n n&agrave;y vẫn vậy. Ở hải qu&acirc;n, Smoker hoặc Garp c&oacute; thể bỏ qua bản chất nghịch ngợm của Luffy.&nbsp;Nhưng cuối c&ugrave;ng xu hướng h&agrave;nh động t&ugrave;y tiện của Luffy sẽ khiến anh ta trở th&agrave;nh một nh&acirc;n vật độc nhất v&ocirc; nhị.&nbsp;</p>\r\n', 'lu1604050770.jpg', 2),
(6, 'Tiếp tục bỏ bê \"đứa con\" One Punch Man, \"cha đẻ\" bộ truyện này đi làm thiết kế nhân vật game?', '<h2>C&oacute; vẻ như &quot;cha đẻ&quot; của One Punch Man kh&ocirc;ng c&ograve;n mặn m&agrave; với &quot;đứa con&quot; tinh thần m&agrave; &ocirc;ng tạo ra nữa.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Mới đ&acirc;y nhất đ&atilde; xuất hiện tựa game &quot;Buddy Mission Bond&quot;. Đ&acirc;y l&agrave; tựa game phi&ecirc;u lưu ho&agrave;n to&agrave;n mới sắp ra mắt tr&ecirc;n Switch. V&agrave; thật bất ngờ khi thiết kế nh&acirc;n vật của game kh&ocirc;ng ai kh&aacute;c ch&iacute;nh l&agrave; t&aacute;c giả của manga&nbsp;<a href=\"https://gamek.vn/tim-kiem.chn?keyword=One%20Punch%20Man\" target=\"_blank\">One Punch Man</a>&nbsp;&ndash; Yusuke Murata. Nintendo đ&atilde; th&ocirc;ng b&aacute;o rằng một tựa game phi&ecirc;u lưu ho&agrave;n to&agrave;n mới mang t&ecirc;n &quot;Buddy Mission Bond&quot;. Đ&acirc;y l&agrave; c&aacute;i t&ecirc;n sẽ sớm ra mắt v&agrave;o ng&agrave;y 29 th&aacute;ng 1 năm 2021 d&agrave;nh cho hệ m&aacute;y Nintendo Switch.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/bv1-16040295266081921324503.jpg\" target=\"_blank\"><img alt=\"Tiếp tục bỏ bê đứa con One Punch Man, cha đẻ bộ truyện này đi làm thiết kế nhân vật game? - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/30/bv1-16040295266081921324503.jpg\" /></a></p>\r\n\r\n<p>One Punch Man li&ecirc;n tiếp bị delay trong khi đ&oacute; th&igrave; Yusuke Murata đang bận việc kh&aacute;c</p>\r\n\r\n<p>Th&ocirc;ng qua trailer game được đăng tải, nhiều người đ&atilde; v&ocirc; c&ugrave;ng bất ngờ khi thiết kế nh&acirc;n vật của game ch&iacute;nh l&agrave; họa sĩ Yusuke Murata. Th&ocirc;ng tin n&agrave;y nhanh ch&oacute;ng được lan truyền mạnh mẽ trong cộng đồng ngươi h&acirc;m mộ của One Punch Man.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/base64-16040295796711565810609.png\" target=\"_blank\"><img alt=\"Tiếp tục bỏ bê đứa con One Punch Man, cha đẻ bộ truyện này đi làm thiết kế nhân vật game? - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/base64-16040295796711565810609.png\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng c&oacute; g&igrave; phải b&agrave;n c&atilde;i về độ đẹp v&agrave; cuốn h&uacute;t của &quot;Buddy Mission Bond&quot; dưới t&agrave;i năng của Yusuke. Nhiều người lộ r&otilde; sự mong đợi về dự &aacute;n game n&agrave;y v&agrave; đảm bảo sẽ trải nghiệm n&oacute; v&agrave;o ng&agrave;y ra mắt. Tất nhi&ecirc;n, vẫn c&oacute; một số t&agrave;i khoản kh&aacute;c &quot;soi m&oacute;i&quot; Yusuke Murata rằng: &quot;Sao sensei kh&ocirc;ng tập trung vẽ truyện đi&quot;. Những lời &quot;c&agrave; khịa&quot; tr&ecirc;n chủ yếu do One Punch Man đang ph&aacute;t h&agrave;nh qu&aacute; mức chậm chạp trong khi c&acirc;u chuyện đang qu&aacute; hấp dẫn. Điều n&agrave;y khiến cho độc giả kh&ocirc;ng khỏi cảm thấy kh&oacute; chịu, chẳng th&agrave; t&aacute;c giả bận hoặc b&iacute; &yacute; tưởng đằng n&agrave;y &ocirc;ng lại c&oacute; vẻ như bỏ b&ecirc; &quot;đứa con tinh thần&quot; của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/tac-gia-one-punch-man-chuyen-sang-thiet-ke-game-7-16040295037761159938147.jpg\" target=\"_blank\"><img alt=\"Tiếp tục bỏ bê đứa con One Punch Man, cha đẻ bộ truyện này đi làm thiết kế nhân vật game? - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/tac-gia-one-punch-man-chuyen-sang-thiet-ke-game-7-16040295037761159938147.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/tac-gia-one-punch-man-chuyen-sang-thiet-ke-game-4-16040294878371755320017.jpg\" target=\"_blank\"><img alt=\"Tiếp tục bỏ bê đứa con One Punch Man, cha đẻ bộ truyện này đi làm thiết kế nhân vật game? - Ảnh 4.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/tac-gia-one-punch-man-chuyen-sang-thiet-ke-game-4-16040294878371755320017.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/tac-gia-one-punch-man-chuyen-sang-thiet-ke-game-5-16040294916579537191.jpg\" target=\"_blank\"><img alt=\"Tiếp tục bỏ bê đứa con One Punch Man, cha đẻ bộ truyện này đi làm thiết kế nhân vật game? - Ảnh 5.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/tac-gia-one-punch-man-chuyen-sang-thiet-ke-game-5-16040294916579537191.jpg\" /></a></p>\r\n', 'lufyy1604050837.jpg', 2),
(7, 'Spoiler đầy đủ One Piece chương 994, đáng buồn tuần sau truyện nghỉ', '<h2>Trong One Piece 994, con g&aacute;i Kaido n&oacute;i sẵn s&agrave;ng hy sinh v&igrave; Momonosuke.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Trang cover tuần n&agrave;y tiếp tục c&acirc;u chuyện về gia đ&igrave;nh nhỏ của Gang Bege. C&oacute; vẻ như phe Gang Bege vẫn chưa tho&aacute;t hẳn khỏi sự truy đuổi của phe hải qu&acirc;n trước đ&oacute;. Trang cover tuần n&agrave;y l&agrave; cảnh con t&agrave;u của băng Bege đang vui vẻ lao đi khỏi sự truy đuổi của phe Hải Qu&acirc;n.</p>\r\n\r\n<p>Bắt đầu&nbsp;<a href=\"https://gamek.vn/one-piece.htm\" target=\"_blank\">One Piece</a>&nbsp;chương 994 l&agrave; khung cảnh Kinemon đang sử dụng hỏa hồ kiếm ph&aacute;i của m&igrave;nh để cầm m&aacute;u cho Kiku bằng c&aacute;ch đốt vết thương. Sau đ&oacute; Kiku đứng dậy cười v&agrave; n&oacute;i c&ocirc; ấy ổn. Ch&uacute;ng ta cũng nh&igrave;n thấy cảnh Kiku đ&atilde; đổi một thanh kiếm ho&agrave;n to&agrave;n mới. C&aacute;c X&iacute;ch Sao kh&aacute;c cũng c&oacute; sự thay đổi nhỏ. C&oacute; vẻ nh&oacute;m X&iacute;ch Sao đ&atilde; c&oacute; chuẩn bị cho t&igrave;nh huống hỏng vũ kh&iacute; n&agrave;y. Chưa r&otilde; những thanh kiếm n&agrave;y l&agrave; do họ mang theo hay cướp được từ qu&acirc;n Kaido.</p>\r\n\r\n<p>Kaido n&oacute;i hắn th&iacute;ch c&aacute;c samurai v&igrave; c&aacute;c samurai đ&atilde; lu&ocirc;n sẵn s&agrave;ng tinh thần để chết. Hắn ta ghen tị với c&aacute;i chết vinh quang của Roger v&agrave; R&acirc;u Trắng, v&igrave; theo hắn, ch&iacute;nh c&aacute;i chết mới khiến đời người trọn vẹn. Sau đ&oacute; Kaido biến trở về h&igrave;nh dạng con người v&agrave; cuộc chiến vẫn tiếp tục. Sau khi trở lại h&igrave;nh người, Kaido tr&ocirc;ng kh&ocirc;ng c&oacute; vết thương n&agrave;o mới, c&aacute;c vết đ&acirc;m xuy&ecirc;n b&agrave;n tay trước đ&oacute; dường như đ&atilde; l&agrave;nh lại ho&agrave;n to&agrave;n.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-1-16039761773221348531077.jpg\" target=\"_blank\"><img alt=\"Spoiler đầy đủ One Piece chương 994, đáng buồn tuần sau truyện nghỉ - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/photo-1-16039761773221348531077.jpg\" /></a></p>\r\n\r\n<p>Chuyển cảnh Nh&oacute;m của Luffy. L&uacute;c n&agrave;y nh&oacute;m Luffy đang chạm tr&aacute;n với 2 t&ecirc;n Gifters Shinuchi c&oacute; tạo h&igrave;nh k&igrave; lạ. Một t&ecirc;n l&agrave; Hamlet, c&oacute; tạo h&igrave;nh như một qu&yacute; &ocirc;ng lịch l&atilde;m nhưng cả th&acirc;n thể của hắn được gắn tr&ecirc;n cổ của một con hươu cao cổ. Một t&ecirc;n Gifters kh&aacute;c l&agrave; Fourtricks c&oacute; h&igrave;nh dạng một con g&agrave; nhưng mặt hắn lại nằm tr&ecirc;n phần đu&ocirc;i g&agrave; . Cả 2 t&ecirc;n đều kh&aacute; yếu v&agrave; dễ d&agrave;ng bị Jinbe lẫn Sanji hạ gục.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-1-16039761787571534998997.jpg\" target=\"_blank\"><img alt=\"Spoiler đầy đủ One Piece chương 994, đáng buồn tuần sau truyện nghỉ - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-1-16039761787571534998997.jpg\" /></a></p>\r\n\r\n<p>Chuyển về sảnh ch&iacute;nh, nhiều người ở sảnh đ&atilde; bị tr&uacute;ng đạn của Queen. Một trong số đ&oacute; l&agrave; Omasa, tr&ugrave;m Yakuza phụ tr&aacute;ch v&ugrave;ng Udon. Chopper n&oacute;i cậu kh&ocirc;ng thể tạo ra thuốc giải độc ngay lập tức trong t&igrave;nh huống n&agrave;y v&igrave; thời gian c&oacute; hạn, nhưng cậu đo&aacute;n chắc hẳn Queen phải mang theo thuốc giải b&ecirc;n m&igrave;nh.</p>\r\n\r\n<p>Queen tuy&ecirc;n bố tất cả những ai bị biến th&agrave;nh quỷ, bao gồm cả những người bị bắn lẫn bị l&acirc;y lan đều sẽ phải chết sau 1 giờ. Những t&ecirc;n Quỷ Băng đang tấn c&ocirc;ng bừa b&atilde;i kh&ocirc;ng ph&acirc;n địch ta, ngay cả thuộc hạ của Kaido v&agrave; những t&ecirc;n samurai của Orochi cũng bị tấn c&ocirc;ng.</p>\r\n\r\n<p>Queen l&uacute;c n&agrave;y đang đứng tr&ecirc;n lầu cao, hắn cầm loa tuy&ecirc;n bố rằng hắn sẽ bắt đầu một tr&ograve; chơi v&agrave; n&eacute;m chai thuốc giải độc duy nhất cho Apoo. Queen ra lệnh cho Apoo phải bảo vệ b&igrave;nh thuốc giải cho đến khi mọi người ở đ&acirc;y chết sạch, nếu kh&ocirc;ng Apoo sẽ bị Queen xử tử. Mọi người trong sảnh lập tức đuổi theo Apoo.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-2-16039761792431062877803.jpg\" target=\"_blank\"><img alt=\"Spoiler đầy đủ One Piece chương 994, đáng buồn tuần sau truyện nghỉ - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/photo-2-16039761792431062877803.jpg\" /></a></p>\r\n\r\n<p>Chương truyện chuyển cảnh tới đường sang khu vực N&atilde;o Phải. L&uacute;c n&agrave;y Yamato đang che trước người b&agrave; Shinobu. Shinobu hiện tại đ&atilde; bị thương sau loạt tấn c&ocirc;ng của qu&acirc;n Sasaki v&agrave; đang nhờ Yamato cứu lấy Momonosuke. Xuất hiện một đoạn hồi tưởng ngắn về l&uacute;c Yamato thời trẻ đang đeo mặt nạ đứng trong đ&aacute;m đ&ocirc;ng v&agrave; quan s&aacute;t buổi h&agrave;nh quyết Oden. Yamato n&oacute;i anh đ&atilde; ở đ&oacute; v&agrave; chứng kiến ​​c&aacute;i chết của Oden lẫn b&agrave;i ph&aacute;t biểu của Shinobu. Mặc d&ugrave; rất muốn gi&uacute;p đỡ nhưng Yamato l&uacute;c đ&oacute; ho&agrave;n to&agrave;n bất lực v&igrave; cậu chưa đủ mạnh.</p>\r\n', 'onep1604050891.jpg', 2),
(8, 'One Piece: Trong những chap tới, năng lực trái ác quỷ hệ Zoan của con gái Kaido sẽ được thể hiện?', '<h2>C&ugrave;ng đến với giả thuyết về năng lực tr&aacute;i &aacute;c quỷ m&agrave; Yamato, con g&aacute;i của Kaido c&oacute; thể đang sở hữu nh&eacute;!</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Như ch&uacute;ng ta đ&atilde; biết trong&nbsp;<a href=\"https://gamek.vn/tim-kiem.chn?keyword=one%20piece%20\" target=\"_blank\">One Piece chap 994</a>, t&ecirc;n chap n&agrave;y l&agrave; &quot;t&ecirc;n gọi kh&aacute;c của ta l&agrave; Yamato&quot;. Nội dung của chương mới n&oacute;i về việc Yamato đ&atilde; đứng ra bảo vệ Momonosuke trước sự tấn c&ocirc;ng của Sasaki, một kẻ trong Tobi Roppo v&agrave; đội qu&acirc;n thiết gi&aacute;p của hắn ta. Ch&iacute;nh từ việc n&agrave;y m&agrave; nhiều độc giả cho rằng năng lực thực sự của Yamato sẽ được thể hiện trong những chap tới đ&acirc;y. Tr&ecirc;n thực tế th&igrave; ngo&agrave;i năng lực thể chất v&agrave; chi&ecirc;u thức L&ocirc;i &Ocirc; B&aacute;t Qu&aacute;i giống hệt cha m&igrave;nh ra th&igrave; cho đến thời điểm hiện tại, Yamato vẫn chưa thể hiện nhiều.</p>\r\n\r\n<p>Vậy c&ugrave;ng ch&uacute;ng t&ocirc;i kh&aacute;m ph&aacute; giả thuyết về năng lực tr&aacute;i &aacute;c quỷ m&agrave; Yamato đang sở hữu nh&eacute;!</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/ya5-1603945395602191338316.jpg\" target=\"_blank\"><img alt=\"One Piece: Trong những chap tới, năng lực trái ác quỷ hệ Zoan của con gái Kaido sẽ được thể hiện? - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/ya5-1603945395602191338316.jpg\" /></a></p>\r\n\r\n<p>Yamato đang ra sức bảo vệ Momonosuke, con trai của Oden</p>\r\n\r\n<p>Theo nh&oacute;m t&aacute;c giả (gồm Đ&agrave;m Thanh - Daisuke - Yukihide) đưa ra giả thuyết n&agrave;y th&igrave; Yamato sở hữu năng lực tr&aacute;i &aacute;c quỷ mang t&ecirc;n Mushi Mushi No Mi, hiểu theo tiếng Việt l&agrave; Tr&aacute;i &aacute;c quỷ loại c&ocirc;n tr&ugrave;ng bọ c&aacute;nh cứng. Giống như c&aacute;c th&agrave;nh vi&ecirc;n kh&aacute;c trong băng B&aacute;ch Th&uacute;, tr&aacute;i &aacute;c quỷ n&agrave;y thuộc hệ Zoan Cổ Đại.</p>\r\n\r\n<p>Theo tiếng Nhật mushi nghĩa l&agrave; c&ocirc;n tr&ugrave;ng, dạng tamamushi c&oacute; nghĩa l&agrave; 1 loại bọ c&aacute;nh cứng, t&ecirc;n tiếng Việt c&oacute; nghĩa l&agrave; bọ ngọc. Tr&aacute;i &aacute;c quỷ Mushi Mushi no Mi đ&atilde; từng xuất hiện ở phần Dressrosa dưới 2 dạng ong của Vivian v&agrave; bọ sừng của Kabu thuộc tộc t&iacute; hon Tontatta. Nh&oacute;m t&aacute;c giả n&agrave;y đ&atilde; đưa ra những luận điểm để khẳng định Yamato đang sở hữu tr&aacute;i &aacute;c quỷ Mushi Mushi No Mi hệ Zoan như sau:</p>\r\n\r\n<p><strong>Mối li&ecirc;n hệ giữa Otama v&agrave; Yamato</strong></p>\r\n\r\n<p>Cả Otama v&agrave; Yamato c&ugrave;ng l&agrave; người quen của Hỏa quyền Ace, c&ugrave;ng gặp Luffy Mũ Rơm v&agrave; cuộc đời của họ sẽ c&oacute; nhiều thay đổi sau khi gặp Luffy. Đặc biệt hơn, t&ecirc;n của 2 nh&acirc;n vật n&agrave;y viết gh&eacute;p lại l&agrave; &quot;OTAMAYAMATO&quot;. Nếu ch&uacute;ng ta viết ngược từ sau lại trước th&igrave; ta vẫn sẽ c&oacute; đ&uacute;ng từ đ&oacute;, c&aacute;i n&agrave;y trong văn ho&aacute; Nhật gọi l&agrave; kaibun (nghệ thuật đảo văn). Đ&acirc;y c&oacute; thể xem như 1 gợi &yacute; về năng lực của Yamato, c&oacute; li&ecirc;n quan đến chữ Tama (ngọc).</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/ya2-16039453850901155810908.jpg\" target=\"_blank\"><img alt=\"One Piece: Trong những chap tới, năng lực trái ác quỷ hệ Zoan của con gái Kaido sẽ được thể hiện? - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/ya2-16039453850901155810908.jpg\" /></a></p>\r\n\r\n<p>Mối li&ecirc;n hệ giữa Otama v&agrave; Yamato</p>\r\n\r\n<p><strong>Gợi &yacute; từ m&agrave;u t&oacute;c</strong></p>\r\n\r\n<p>Như ch&uacute;ng ta đ&atilde; biết th&igrave; trong ảnh b&igrave;a tạp ch&iacute; Weekly Shonen Jump số 46 bất ngờ xuất hiện Yamato đồng h&agrave;nh c&ugrave;ng&nbsp;<a href=\"https://gamek.vn/tim-kiem.chn?keyword=one%20piece%20\" target=\"_blank\">Luffy</a>. Yamato đ&atilde; c&oacute; m&agrave;n xuất hiện nguy&ecirc;n h&igrave;nh full m&agrave;u. V&agrave; ch&uacute;ng ta h&atilde;y để &yacute; m&agrave;u t&oacute;c ombre của Yamato, với xanh phần đu&ocirc;i. Ombre l&agrave; kiểu nhuộm kh&oacute;i, tuỳ theo g&oacute;c nh&igrave;n m&agrave; m&agrave;u sẽ kh&aacute;c đi ch&uacute;t. Tamamushi cũng l&agrave; loại bọ c&oacute; m&agrave;u &oacute;ng &aacute;nh xanh tr&ecirc;n lưng, m&agrave;u kh&aacute;c cũng sẽ c&oacute; kh&aacute;c biệt kha kh&aacute; dựa v&agrave;o g&oacute;c nh&igrave;n. Tamamushi th&igrave; kh&oacute; gọi ch&iacute;nh x&aacute;c m&agrave;u sắc của ch&uacute;ng, điều n&agrave;y cũng tương tự như Yamato, l&uacute;c đầu cả Kaido cũng gọi l&agrave; &quot;con trai của ta&quot; nhưng ho&aacute; ra lại l&agrave; một c&ocirc; con g&aacute;i.</p>\r\n', 'ya5-16039453956021913383161604050930.jpg', 2),
(9, 'Chỉ mất 10 ngày, Kimetsu no Yaiba trở thành bộ phim đạt doanh thu 10 tỷ yên nhanh nhất trong lịch sử Nhật Bản', '<h2>Sức h&uacute;t v&agrave; độ n&oacute;ng của Kimetsu no Yaiba &ndash; The Movie: Mugen Train đ&atilde; được thể hiện qua một kỷ lục v&ocirc; tiền kho&aacute;ng hậu về doanh thu.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Vào h&ocirc;m thứ hai vừa qua, TOHO và Aniplex đã chính thức xác nh&acirc;̣n rằng b&ocirc;̣ phim đi&ecirc;̣n ảnh Demon Slayer &ndash;&nbsp;<a href=\"https://gamek.vn/tim-kiem.chn?keyword=Kimetsu%20No%20Yaiba\" target=\"_blank\">Kimetsu no Yaiba</a>&nbsp;&ndash; The Movie: Mugen Train (Kimetsu no Yaiba: Mugen Ressha-Hen) có g&acirc;̀n 8 tri&ecirc;̣u lượt vé được bán ra và doanh thu đã vượt ngưỡng 10 tỷ y&ecirc;n trong 10 ngày đ&acirc;̀u ti&ecirc;n c&ocirc;ng chi&ecirc;́u. Một con số m&agrave; theo c&aacute;c nh&agrave; l&agrave;m phim l&agrave; vượt qua sức tưởng tượng của họ.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/yb1-16039475040081080201912.jpg\" target=\"_blank\"><img alt=\"Chỉ mất 10 ngày, Kimetsu no Yaiba trở thành bộ phim đạt doanh thu 10 tỷ yên nhanh nhất trong lịch sử Nhật Bản - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/yb1-16039475040081080201912.jpg\" /></a></p>\r\n\r\n<p>Kimetsu no Yaiba &ndash; The Movie: Mugen Train ch&iacute;nh thức trở th&agrave;nh bộ phim đạt 10 tỷ y&ecirc;n nhanh nhất Nhật Bản</p>\r\n\r\n<p>Trong 10 ngày đ&acirc;̀u ti&ecirc;n, b&ocirc;̣ phim đã có 7.983.442 vé được bán ra với 10.754.232.550 y&ecirc;n (g&acirc;̀n 2 nghìn 400 tỉ đ&ocirc;̀ng Việt Nam). N&oacute; đã trở thành b&ocirc;̣ phim đạt doanh thu 10 tỷ y&ecirc;n nhanh nh&acirc;́t tại Nh&acirc;̣t Bản từ trước đến nay. Đánh bại kỷ lục của Spirited Away (hi&ecirc;̣n đang là b&ocirc;̣ phim có doanh thu cao nh&acirc;́t mọi thời đại Nh&acirc;̣t Bản với 30,8 tỉ y&ecirc;n). Spirited Away đã phải m&acirc;́t 25 ngày đ&ecirc;̉ đạt 10 tỷ vào năm 2001.</p>\r\n\r\n<p>Trong 10 ngày đ&acirc;̀u ti&ecirc;n, b&ocirc;̣ phim đã có 7.983.442 vé được bán ra với 10.754.232.550 y&ecirc;n (g&acirc;̀n 2 nghìn 400 tỉ đ&ocirc;̀ng). N&oacute; đã trở thành b&ocirc;̣ phim đạt doanh thu 10 tỷ y&ecirc;n nhanh nh&acirc;́t tại Nh&acirc;̣t Bản. Kimetsu no Yaiba &ndash; The Movie: Mugen Train đ&atilde; đánh bại kỷ lục của Spirited Away (hi&ecirc;̣n đang là b&ocirc;̣ phim có doanh thu cao nh&acirc;́t mọi thời đại Nh&acirc;̣t Bản với 30,8 tỉ y&ecirc;n). Spirited Away đã phải m&acirc;́t 25 ngày đ&ecirc;̉ đạt 10 tỷ vào năm 2001, trong khi đ&oacute; Kimetsu no Yaiba &ndash; The Movie: Mugen Train chỉ mất c&oacute; đ&uacute;ng 10 ng&agrave;y</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/yb3-16039475115801227163747.jpg\" target=\"_blank\"><img alt=\"Chỉ mất 10 ngày, Kimetsu no Yaiba trở thành bộ phim đạt doanh thu 10 tỷ yên nhanh nhất trong lịch sử Nhật Bản - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/yb3-16039475115801227163747.jpg\" /></a></p>\r\n\r\n<p>Spirited Away đã phải m&acirc;́t 25 ngày đ&ecirc;̉ đạt 10 tỷ vào năm 2001</p>\r\n\r\n<p>Vào hai ngày cu&ocirc;́i tu&acirc;̀n vừa r&ocirc;̀i, b&ocirc;̣ phim đã thu hút th&ecirc;m 2,2 tri&ecirc;̣u lượt vé với 3 tỷ y&ecirc;n được b&ocirc;̉ sung vào doanh thu n&agrave;y. Hơn l&uacute;c n&agrave;o hết c&aacute;c kh&aacute;n giả v&agrave; c&aacute;c nh&agrave; l&agrave;m phim đang mong chờ Kimetsu no Yaiba &ndash; The Movie: Mugen Train sẽ tiếp tục x&ocirc; đổ kỷ lục 30,8 tỷ y&ecirc;n của Spirited Away, bộ phim đang nắm giữ kỷ lục doanh thu cao nhất mọi thời đại tại Nhật Bản.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/yb2-1603947508130811571304.jpg\" target=\"_blank\"><img alt=\"Chỉ mất 10 ngày, Kimetsu no Yaiba trở thành bộ phim đạt doanh thu 10 tỷ yên nhanh nhất trong lịch sử Nhật Bản - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/yb2-1603947508130811571304.jpg\" /></a></p>\r\n\r\n<p>Kimetsu no Yaiba &ndash; The Movie: Mugen Train chỉ mất 10 ng&agrave;y để đạt con số 10 tỷ y&ecirc;n</p>\r\n\r\n<p>Hơn l&uacute;c n&agrave;o hết kh&aacute;n giả Việt Nam đang chờ đợi thời khắc Kimetsu no Yaiba &ndash; The Movie: Mugen Train về tới Việt Nam. L&uacute;c đ&oacute; chắc hẳn ch&uacute;ng ta sẽ được thưởng thức một bộ phim anime tuyệt vời, v&agrave; tổng doanh thu của&nbsp;<a href=\"https://gamek.vn/tim-kiem.chn?keyword=Kimetsu%20No%20Yaiba\" target=\"_blank\">Kimetsu no Yaiba</a>&nbsp;&ndash; The Movie: Mugen Train tr&ecirc;n to&agrave;n thế giới chắc chắn sẽ la rất khủng.</p>\r\n', 'yb1-160394750400810802019121604051078.jpg', 2),
(10, 'BLV Hoàng Luân và những nỗi khổ không nói thành lời - \"Có con gái mới khiến tôi bớt stress\"', '<h2>BLV Ho&agrave;ng Lu&acirc;n mới đ&acirc;y đ&atilde; c&oacute; những chia sẻ về c&ocirc;ng việc cũng như cuộc sống sau khi c&oacute; con g&aacute;i đầu l&ograve;ng.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>L&agrave; người gắn b&oacute; với nền&nbsp;<a href=\"https://gamek.vn/lmht.htm\" target=\"_blank\">LMHT</a>&nbsp;Việt Nam từ những ng&agrave;y đầu ti&ecirc;n,&nbsp;<a href=\"https://gamek.vn/blv-hoang-luan.htm\" target=\"_blank\">BLV Ho&agrave;ng Lu&acirc;n</a>&nbsp;đ&atilde; nhận được rất nhiều t&igrave;nh cảm của game thủ Việt với phong c&aacute;ch b&igrave;nh luận vừa m&aacute;u lửa, h&agrave;i hước m&agrave; kh&ocirc;ng hề thiếu t&iacute;nh chuy&ecirc;n m&ocirc;n. V&agrave; c&aacute;ch đ&acirc;y &iacute;t ng&agrave;y, BLV Ho&agrave;ng Lu&acirc;n đ&atilde; nhận th&ecirc;m một tin cực vui đ&oacute; l&agrave; l&ecirc;n chức bố khi đ&oacute;n con g&aacute;i đầu l&ograve;ng v&agrave;o ng&agrave;y 23/10 vừa rồi.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/photo-1-16040459252341607516345.jpg\" target=\"_blank\"><img alt=\"BLV Hoàng Luân và những nỗi khổ không nói thành lời - Có con gái mới khiến tôi bớt stress - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/photo-1-16040459252341607516345.jpg\" /></a></p>\r\n\r\n<p>H&igrave;nh ảnh về b&eacute; g&aacute;i đầu l&ograve;ng được BLV Ho&agrave;ng Lu&acirc;n chia sẻ</p>\r\n\r\n<p>Mặc d&ugrave; vừa được người h&acirc;m mộ y&ecirc;u mến, gia đ&igrave;nh ch&agrave;o đ&oacute;n th&ecirc;m th&agrave;nh vi&ecirc;n mới, tuy nhi&ecirc;n cuộc sống của BLV Ho&agrave;ng Lu&acirc;n dường như lại diễn ra kh&ocirc;ng được như &yacute;. Mới đ&acirc;y BLV Ho&agrave;ng Lu&acirc;n đ&atilde; chia sẻ tr&ecirc;n stream rằng anh đ&atilde; v&agrave; đang gặp rất nhiều stress trong thời gian vừa qua. Từ khi c&oacute; con g&aacute;i đầu l&ograve;ng th&igrave; anh mới bớt căng thẳng v&agrave; t&igrave;m thấy niềm vui trong cuộc sống.</p>\r\n\r\n<p>Tự động ph&aacute;t sau</p>\r\n\r\n<p>3</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pelu aka BLV Ho&agrave;ng Lu&acirc;n đ&atilde; c&oacute; những lời chia sẻ v&ocirc; c&ugrave;ng ch&acirc;n th&agrave;nh ở trong buổi stream mới nhất</p>\r\n\r\n<p>BLV Ho&agrave;ng Lu&acirc;n:&nbsp;<em>&quot;Thật ra c&oacute; nhiều vấn đề trong cuộc sống m&igrave;nh cảm thấy &#39;no hope&#39; lắm, kh&ocirc;ng c&oacute; giải ph&aacute;p hay kh&ocirc;ng thể vượt qua được. M&igrave;nh đ&atilde; t&igrave;m nhiều c&aacute;ch kh&aacute;c nhau để vượt qua nhưng kh&ocirc;ng được, thật may mắn l&agrave; c&oacute; b&eacute; Xo&agrave;i (t&ecirc;n gọi trong gia đ&igrave;nh của con g&aacute;i BLV Ho&agrave;ng Lu&acirc;n) n&ecirc;n mới c&oacute; thể vượt qua được.</em></p>\r\n\r\n<p><em>Trước đ&acirc;y m&igrave;nh từng t&igrave;m c&aacute;ch giải tỏa stress bằng c&aacute;ch chơi game, tuy nhi&ecirc;n n&oacute; lại kh&ocirc;ng giải quyết được những vấn đề lớn lao hơn. Cuộc sống đ&acirc;u giống cuộc đời đ&uacute;ng kh&ocirc;ng? Ai cũng c&oacute; vấn đề m&agrave;.&nbsp;C&aacute;c bạn đừng nghĩ lấy vợ sinh con l&agrave; c&aacute;ch để xả stress nha, đ&acirc;y l&agrave; việc cực k&igrave; quan trọng m&agrave; ch&uacute;ng ta phải suy nghĩ thực sự kỹ c&agrave;ng rồi mới đưa ra quyết định. Đ&acirc;y l&agrave; lời khuy&ecirc;n ch&acirc;n th&agrave;nh của m&igrave;nh đ&oacute;.</em></p>\r\n\r\n<p><em>Đ&atilde; 10 ng&agrave;y rồi m&igrave;nh mới l&ecirc;n stream lu&ocirc;n đ&oacute;, từ khi c&oacute; b&eacute; Xo&agrave;i th&igrave; mọi thời gian đều d&agrave;nh cho việc chăm con lu&ocirc;n. Đ&acirc;y mới chỉ l&agrave; khởi đầu th&ocirc;i m&agrave; m&igrave;nh cảm thấy qu&atilde;ng đường ph&iacute;a trước cực k&igrave; d&agrave;i, đ&acirc;y l&agrave; tr&aacute;ch nhiệm v&ocirc; c&ugrave;ng lớn m&agrave; m&igrave;nh nghĩ kh&ocirc;ng phải ai cũng th&iacute;ch hợp để g&aacute;nh lấy&quot;.</em></p>\r\n', 'luan1604051441.jpg', 1),
(11, 'Sự thật về SofM: Nghiện \"tà tưa\", mê bún đậu và thói quen donate rất \"mát tay\" cho bại tướng', '<h2>B&ecirc;n cạnh vẻ ngo&agrave;i c&oacute; phần đ&aacute;ng y&ecirc;u của m&igrave;nh, SofM c&ograve;n được biết đến l&agrave; người c&oacute; t&acirc;m hồn ăn uống cũng như th&oacute;i quen donate đầy th&uacute; vị.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Ch&agrave;ng tuyển thủ người Việt đầu ti&ecirc;n tham dự trận Chung kết LMHT Thế giới, SofM kh&ocirc;ng chỉ được y&ecirc;u mến nhờ c&aacute;c m&agrave;n tr&igrave;nh diễn trong giải đấu hay l&uacute;c đ&aacute;nh rank cũng như những s&aacute;ng kiến tạo n&ecirc;n những lối chơi mới. Anh c&ograve;n được y&ecirc;u mến nhờ t&iacute;nh c&aacute;ch khi&ecirc;m tốn v&agrave; một t&acirc;m hồn m&ecirc; ăn uống v&ocirc; tận.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/photo-1-16040354960581085456625.jpg\" target=\"_blank\"><img alt=\"Sự thật về SofM: Nghiện tà tưa, mê bún đậu và thói quen donate rất mát tay cho bại tướng - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/photo-1-16040354960581085456625.jpg\" /></a></p>\r\n\r\n<p>Theo người đồng đội cũ ở Full Louis, Vũ &quot;LoliTa&quot; Ho&agrave;ng Việt chia sẻ rằng SofM dễ nu&ocirc;i, g&igrave; cũng ăn được, g&igrave; cũng uống được, m&agrave; lại c&ograve;n lắm nữa. Chơi game đ&ecirc;m g&igrave; một đ&ecirc;m Duy ăn 3 - 4 lần m&igrave;/ cơm/ b&aacute;nh m&igrave; v&agrave; uống cả k&eacute;t Sting.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/photo-1-1604035497657380844155.jpg\" target=\"_blank\"><img alt=\"Sự thật về SofM: Nghiện tà tưa, mê bún đậu và thói quen donate rất mát tay cho bại tướng - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/photo-1-1604035497657380844155.jpg\" /></a></p>\r\n\r\n<p>Sau đ&oacute;, khi sang Trung Quốc, c&oacute; lẽ tr&agrave; sữa ch&iacute;nh l&agrave; ch&acirc;n &aacute;i của ch&agrave;ng&nbsp;&quot;Duy Cầu Giấy&quot;. Đ&acirc;y l&agrave; m&oacute;n kho&aacute;i khẩu của anh v&agrave; thường được Ban Huấn luyện của đội tuyển cũ l&agrave; Snake eSports d&ugrave;ng để kh&iacute;ch lệ tinh thần.&nbsp;</p>\r\n\r\n<p>T&igrave;nh y&ecirc;u của anh d&agrave;nh cho &quot;t&agrave; tưa&quot; m&atilde;nh liệt đến nổi l&uacute;c SofM chuyển sang thi đấu cho Suning Gaming, Snake eSports đ&atilde; kh&ocirc;ng qu&ecirc;n đăng status nhắc nhở Suning phải chu cấp tr&agrave; sữa cho cậu.</p>\r\n', 'sofm1604051486.jpg', 1),
(12, 'Hướng dẫn làm chủ Seraphine - Nữ idol có khả năng giao tranh cực kì mạnh của LMHT', '<h2>Seraphine đ&atilde; ch&iacute;nh thức ra mắt LMHT v&agrave;o ng&agrave;y h&ocirc;m nay - 30/10 v&agrave; h&atilde;y t&igrave;m hiểu xem l&agrave;m thế n&agrave;o để sử dụng tốt vị tướng n&agrave;y.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p><strong>Đ&aacute;nh gi&aacute; chung</strong></p>\r\n\r\n<p>Bộ kỹ năng của&nbsp;<a href=\"https://gamek.vn/seraphine.htm\" target=\"_blank\">Seraphine</a>&nbsp;bạn c&oacute; thể t&igrave;m hiểu ở&nbsp;<a href=\"https://gamek.vn/lo-dien-clip-ky-nang-cua-seraphine-sona-20-cua-lmht-20201012195508209.chn\" target=\"_blank\">Đ&Acirc;Y</a>, nh&igrave;n chung th&igrave; vị tướng n&agrave;y l&agrave; một ph&aacute;p sư mang thi&ecirc;n hướng hỗ trợ với t&iacute;nh đa dụng cực k&igrave; cao trong&nbsp;<a href=\"https://gamek.vn/lmht.htm\" target=\"_blank\">LMHT</a>. Seraphine c&oacute; thể chơi tốt cả ở vị tr&iacute; đường giữa lẫn hỗ trợ. Với bộ kỹ năng hướng tới việc buff cho đồng minh, cung cấp c&aacute;c hiệu ứng khống chế kh&oacute; chịu, điểm mạnh nhất của Seraphine l&agrave; khả năng giao tranh.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/14/kda-seraphine-3-1602644190774172186342.jpg\" target=\"_blank\"><img alt=\"Hướng dẫn làm chủ Seraphine - Nữ idol có khả năng giao tranh cực kì mạnh của LMHT - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/14/kda-seraphine-3-1602644190774172186342.jpg\" /></a></p>\r\n\r\n<p>Riot Games đ&atilde; ban cho Seraphine bộ kỹ năng mang t&iacute;nh đa dụng cực k&igrave; cao</p>\r\n\r\n<p>Điểm yếu của Seraphine đường giữa đ&oacute; l&agrave; khả năng dọn l&iacute;nh kh&ocirc;ng qu&aacute; mạnh ở đầu trận, v&igrave; vậy c&ocirc; n&agrave;ng n&agrave;y dễ bị &eacute;p l&iacute;nh v&agrave;o trụ v&agrave; chơi kh&aacute; bị động ở những cấp độ đầu. Nếu chơi ở vị tr&iacute; hỗ trợ, Seraphine cũng c&oacute; nhược điểm giống như c&aacute;c support buff kh&aacute;c l&agrave; s&aacute;t thương đầu game kh&ocirc;ng cao. Nếu gặp một bộ đ&ocirc;i đường dưới c&oacute; t&iacute;nh chiến đấu mạnh, Seraphine rất kh&oacute; c&oacute; thể đi đường an to&agrave;n.</p>\r\n\r\n<p><strong>Ngọc bổ trợ</strong></p>\r\n\r\n<p>Với một vị tướng vừa c&oacute; khả năng g&acirc;y s&aacute;t thương lẫn buff cho đồng minh, Triệu Hồi Aery l&agrave; điểm ngọc th&iacute;ch hợp nhất với Seraphine. Bạn cũng c&oacute; thể chọn Thi&ecirc;n Thạch B&iacute; Ẩn nếu muốn cấu đối thủ c&ugrave;ng đường nhiều nhất. C&oacute; một lưu &yacute; l&agrave; hai điểm Rải Băng Năng Lượng v&agrave; Thăng Tiến Sức Mạnh ở nh&aacute;nh Ph&eacute;p Thuật l&agrave; cực k&igrave; quan trọng với vị tướng n&agrave;y. L&yacute; do l&agrave; bởi Seraphine rất cần năng lượng v&agrave; hồi chi&ecirc;u để tung kỹ năng li&ecirc;n tục nhằm k&iacute;ch hoạt li&ecirc;n tục c&aacute;c hiệu ứng tạo khi&ecirc;n, l&agrave;m chậm...</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/aery-16040289257171226991626.png\" target=\"_blank\"><img alt=\"Hướng dẫn làm chủ Seraphine - Nữ idol có khả năng giao tranh cực kì mạnh của LMHT - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/aery-16040289257171226991626.png\" /></a></p>\r\n\r\n<p>Bảng ngọc tối ưu cho Seraphine ở thời điểm hiện tại, bạn c&oacute; thể đổi sang Thi&ecirc;n Thạch B&iacute; Ẩn nếu cần cấu m&aacute;u nhiều hơn khi đi đường</p>\r\n\r\n<p>Nếu chơi ở vị tr&iacute; đường giữa th&igrave; c&oacute; một lựa chọn tương đối tốt kh&aacute;c đ&oacute; l&agrave; S&aacute;ch Ph&eacute;p, n&oacute; sẽ gi&uacute;p cho khả năng tạo đột biến của Seraphine tốt hơn hẳn với việc lu&acirc;n chuyển ph&eacute;p bổ trợ.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/sp-1604028966522463183576.png\" target=\"_blank\"><img alt=\"Hướng dẫn làm chủ Seraphine - Nữ idol có khả năng giao tranh cực kì mạnh của LMHT - Ảnh 3.\" src=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/sp-1604028966522463183576.png\" /></a></p>\r\n\r\n<p>S&aacute;ch Ph&eacute;p cũng l&agrave; một lựa chọn hay cho vị tướng n&agrave;y khi đi đường giữa</p>\r\n\r\n<p><strong>Trang bị</strong></p>\r\n\r\n<p>Ở vị tr&iacute; đường giữa, Vọng &Acirc;m Luden l&agrave; item lớn đầu ti&ecirc;n m&agrave; Seraphine cần phải c&oacute; bởi trang bị n&agrave;y sẽ cải thiện điểm yếu dọn l&iacute;nh tương đối k&eacute;m của c&ocirc; n&agrave;ng. Trang bị lớn thứ hai m&agrave; game thủ n&ecirc;n hướng tới đ&oacute; l&agrave; Song Kiếm Tai Ương bởi n&oacute; tương t&aacute;c tốt với nội tại của Seraphine. Những m&oacute;n đồ sau đ&oacute; m&agrave; Seraphine c&oacute; thể sử dụng sẽ tương tự với c&aacute;c ph&aacute;p sự kh&aacute;c như Mũ Ph&ugrave; Thủy, Đồng Hồ C&aacute;t hay Trượng Hư V&ocirc;...</p>\r\n', 'sere1604051571.jpg', 1),
(13, 'CS:GO - Bom tấn thị trường chuyển nhượng phát nổ: siêu sao NiKo chính thức đầu quân cho G2 Esports', '<h2>Đ&acirc;y chắc chắn sẽ l&agrave; một trong những bản hợp đồng lớn nhất năm 2020 của nền CS:GO chuy&ecirc;n nghiệp.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Sau gần 2 th&aacute;ng đ&agrave;m ph&aacute;n th&igrave; v&agrave;o đ&ecirc;m qua,&nbsp;<a href=\"https://gamek.vn/g2-esports.htm\" target=\"_blank\">G2 Esports</a>&nbsp;đ&atilde; th&ocirc;ng b&aacute;o ch&iacute;nh thức c&oacute; được sự phục vụ của&nbsp;<a href=\"https://gamek.vn/niko.htm\" target=\"_blank\">NiKo</a>, si&ecirc;u sao 23 tuổi người Bosnia. Chi tiết của bản hợp đồng n&agrave;y kh&ocirc;ng được tiết lộ, nhưng theo th&ocirc;ng b&aacute;o của G2 Esports th&igrave; đ&acirc;y l&agrave; vụ chuyển nhượng &quot;đắt gi&aacute; nhất trong lịch sử&nbsp;<a href=\"https://gamek.vn/csgo.htm\" target=\"_blank\">CS:GO</a>&quot;. Với việc chuyển sang G2 th&igrave; NiKo sẽ được thi đấu c&ugrave;ng người anh em họ huNter của m&igrave;nh, một ước mơ đ&atilde; được cả hai n&oacute;i tới rất nhiều lần trong những năm gần đ&acirc;y.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/30/photo-1-1604028324906472248039.jpg\" target=\"_blank\"><img alt=\"CS:GO - Bom tấn thị trường chuyển nhượng phát nổ: siêu sao NiKo chính thức đầu quân cho G2 Esports - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/30/photo-1-1604028324906472248039.jpg\" /></a></p>\r\n\r\n<p>2 anh em NiKo v&agrave; huNter trong m&agrave;u &aacute;o G2 Esports</p>\r\n\r\n<p>Hiện vẫn chưa r&otilde; ai sẽ l&agrave; c&aacute;i t&ecirc;n trong đội h&igrave;nh G2 Esports phải nhường vị tr&iacute; cho NiKo, tuy nhi&ecirc;n nhiều khả năng đ&oacute; sẽ l&agrave; AmaNEk hoặc Jackz khi hai player n&agrave;y đều đang kh&ocirc;ng c&oacute; được phong độ tốt nhất trong những th&aacute;ng gần đ&acirc;y v&agrave; thi đấu c&ugrave;ng vị tr&iacute; với NiKo. Trong th&ocirc;ng b&aacute;o của m&igrave;nh, G2 cũng khẳng định: &quot;Trong những tuần sắp tới ch&uacute;ng t&ocirc;i sẽ l&agrave;m việc với team để quyết định đội h&igrave;nh tối ưu nhất cho mỗi trận đấu.&quot;</p>\r\n\r\n<p>Nối bước NiKo, coach YNk cũng đ&atilde; th&ocirc;ng b&aacute;o chia tay tổ chức n&agrave;y. YNk chia sẻ quyết định n&agrave;y được đưa ra sau nhiều nỗ lực kh&ocirc;ng th&agrave;nh c&ocirc;ng trong việc giải quyết v&agrave; sửa chữa c&aacute;c vấn đề của team v&agrave; &ocirc;ng hy vọng hy vọng rằng sự rời đi của &ocirc;ng v&agrave; NiKo sẽ l&agrave; cơ hội tốt để FaZe Clan khắc phục tận gốc c&aacute;c vấn đề nội bộ v&agrave; x&acirc;y dựng lại một đội h&igrave;nh ph&ugrave; hợp hơn. Để thay thế vị tr&iacute; NiKo, Olofmeister sẽ quay trở lại stand-in cho team trong c&aacute;c trận đấu sắp tới. Huyền thoại người Thụy Điển n&agrave;y đ&atilde; quyết định tạm nghỉ thi đấu chuy&ecirc;n nghiệp v&agrave;o giữa th&aacute;ng 5 vừa rồi v&igrave; c&aacute;c l&yacute; do sức khỏe v&agrave; sau một thời gian d&agrave;i kh&ocirc;ng c&oacute; được phong độ tốt nhất.</p>\r\n', 'photo-1-16040283249064722480391604051633.jpg', 1);
INSERT INTO `tbl_post` (`id_post`, `title_post`, `content_post`, `image_post`, `id_category_post`) VALUES
(14, 'Cha đẻ của LMHT nói gì về SofM (P2): Nhà vô địch của đại chúng', '<h2>H&atilde;y c&ugrave;ng đến với b&agrave;i ph&acirc;n t&iacute;ch của ch&iacute;nh Riot Games về SofM - Ng&ocirc;i sao số 1 tại CKTG 2020.</h2>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Thế nhưng, lối chơi của&nbsp;<a href=\"https://gamek.vn/sofm.htm\" target=\"_blank\">SofM</a>&nbsp;kh&ocirc;ng phải l&agrave; thứ duy nhất khiến anh ta trở th&agrave;nh nh&acirc;n vật c&oacute; ảnh hưởng. Tầm phủ s&oacute;ng của SofM c&oacute; thể được nh&igrave;n thấy trong c&aacute;c fandom của anh ta, v&agrave; với tư c&aacute;ch &quot;người đại diện&quot; duy nhất cho&nbsp;<a href=\"https://gamek.vn/lmht.htm\" target=\"_blank\">LMHT</a>&nbsp;Việt Nam. SofM đến LPL từ năm 2016, từng chơi cho Snake Esports - nay l&agrave; LNG Esports, trong gần 4 năm trước khi chuyển sang Suning, một qu&atilde;ng thời gian v&ocirc; c&ugrave;ng chật vật để t&igrave;m kiếm th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p>Giờ đ&acirc;y, trong lần đầu tham dự một giải đấu quốc tế, trong số hơn 100 tuyển thủ g&oacute;p mặt tại CKTG 2020, SofM trở th&agrave;nh người Việt Nam duy nhất g&oacute;p mặt, khi 2 đại diện của&nbsp;<a href=\"https://gamek.vn/vcs.htm\" target=\"_blank\">VCS</a>&nbsp;đ&atilde; kh&ocirc;ng thể tham gia giải đấu. Đ&acirc;y l&agrave; một tin đau l&ograve;ng với c&aacute;c fan h&acirc;m mộ, nhưng như một niềm an ủi nho nhỏ, giữa nghịch cảnh đ&oacute;, khu vực n&agrave;y đ&atilde; được đại diện bởi ng&ocirc;i sao vĩ đại nhất m&agrave; họ sản sinh: SofM.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-1-16039715038561517206499.jpeg\" target=\"_blank\"><img alt=\"Cha đẻ của LMHT nói gì về SofM (P2): Nhà vô địch của đại chúng - Ảnh 1.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/photo-1-16039715038561517206499.jpeg\" /></a></p>\r\n\r\n<p>Người h&acirc;m mộ của SofM thực sự đ&atilde; bỏ rất nhiều thời gian để ủng hộ v&agrave; xem anh thi đấu. Theo Esports Charts, hầu hết c&aacute;c trận tứ kết đều c&oacute; khoảng 250.000 lượng người xem đến từ Việt Nam, nhưng ri&ecirc;ng trận đấu giữa SN v&agrave; JDG, con số n&agrave;y l&agrave; 380.000. C&oacute; tới 130.000 kh&aacute;n giả c&oacute; mặt trước m&agrave;n h&igrave;nh chỉ để chờ đợi SofM thi đấu.</p>\r\n\r\n<p>SofM cũng l&agrave; một huyền thoại v&agrave; l&agrave; h&igrave;nh mẫu m&agrave; nhiều tuyển thủ chuy&ecirc;n nghiệp của VCS hướng đến, th&agrave;nh c&ocirc;ng của anh đang th&uacute;c đẩy sự ph&aacute;t triển của khu vực. Sau MSI 2019, Riot Games đ&atilde; trao đổi với một người quản l&yacute; c&oacute; kinh nghiệm s&acirc;u sắc về VCS - &Ocirc;ng Nguyễn Vũ Đ&ocirc;n, người từng l&agrave;m việc c&ugrave;ng những đội tuyển h&agrave;ng đầu khu vực, như EVOS Esports tại MSI 2018 v&agrave; Phong Vũ Buffalo (nay l&agrave; Saigon Buffalo) tại CKTG 2018 v&agrave; MSI 2019. &Ocirc;ng Đ&ocirc;n chia sẻ về tầm ảnh hưởng của những tuyển thủ như SofM khi họ rời VCS để thi đấu tại c&aacute;c giải quốc tế:</p>\r\n\r\n<p><em>&quot;Người ta sẽ nh&igrave;n thấy điều đ&oacute; v&agrave; thầm nhủ: &#39;Một ng&agrave;y n&agrave;o đ&oacute;, nếu t&ocirc;i đủ nỗ lực, t&ocirc;i c&oacute; thể kiếm được 10.000 USD một th&aacute;ng! T&ocirc;i c&oacute; thể chơi ở H&agrave;n Quốc, ở Trung Quốc, t&ocirc;i c&oacute; thể đi bất cứ đ&acirc;u tr&ecirc;n thế giới v&agrave; thay đổi cuộc sống của m&igrave;nh.&#39; Nếu họ thấy điều đ&oacute;, nếu họ chứng kiến thần tượng, h&igrave;nh mẫu của bản th&acirc;n l&agrave;m được điều đ&oacute;, họ cũng sẽ c&oacute; động lực để thực hiện n&oacute;. Trước đ&acirc;y, kh&ocirc;ng một ai thực hiện được những điều kể tr&ecirc;n, v&igrave; vậy cũng kh&ocirc;ng c&oacute; ai d&aacute;m mơ tưởng rằng một ng&agrave;y n&agrave;o đ&oacute;, khu vực Trung Quốc lại mong muốn một trong số ch&uacute;ng t&ocirc;i thi đấu cho họ.&quot;</em>&nbsp;- &Ocirc;ng giải th&iacute;ch.</p>\r\n\r\n<p><a href=\"https://gamek.mediacdn.vn/133514250583805952/2020/10/29/photo-1-16039715289901577876871.jpeg\" target=\"_blank\"><img alt=\"Cha đẻ của LMHT nói gì về SofM (P2): Nhà vô địch của đại chúng - Ảnh 2.\" src=\"https://gamek.mediacdn.vn/thumb_w/640/133514250583805952/2020/10/29/photo-1-16039715289901577876871.jpeg\" /></a></p>\r\n\r\n<p><em>&quot;Điều đ&oacute; kiến c&aacute;c tuyển thủ trẻ tin rằng đ&acirc;y l&agrave; một h&agrave;nh tr&igrave;nh sự nghiệp nghi&ecirc;m t&uacute;c. Sau đ&oacute;, họ sẽ c&oacute; một c&aacute;i t&ecirc;n để khoe với bố mẹ m&igrave;nh: &#39;Nh&igrave;n k&igrave;a bố! Anh ch&agrave;ng n&agrave;y chơi game v&agrave; kiếm được 10 ngh&igrave;n đ&ocirc; mỗi th&aacute;ng. Con c&oacute; thể l&agrave;m được như vậy, con c&oacute; khả năng. H&atilde;y để con thử sức. Trong một v&agrave;i năm, con c&oacute; thể đạt được điều đ&oacute;!&#39; N&oacute; kh&ocirc;ng giống với việc họ chỉ nghe ch&uacute;ng t&ocirc;i n&oacute;i, giờ đ&acirc;y họ đ&atilde; c&oacute; một c&aacute;i t&ecirc;n đủ sức thuyết phục. C&aacute;i t&ecirc;n đ&oacute; đủ lớn để xuất hiện tr&ecirc;n s&oacute;ng truyền h&igrave;nh quốc gia. V&agrave; sau đ&oacute; ch&uacute;ng ta sẽ c&oacute; nhiều tuyển thủ hơn nữa, ch&uacute;ng ta sẽ c&oacute; một m&ocirc;i trường cạnh tranh hơn nhiều. Điều đ&oacute; tạo ra nhiều sự th&uacute; vị hơn. Thật đ&aacute;ng tự h&agrave;o khi thấy c&aacute;c tuyển thủ của ch&uacute;ng t&ocirc;i chuyển sang thi đấu ở một khu vực lớn.&quot;</em></p>\r\n\r\n<p>Quan điểm của nh&acirc;n vật n&agrave;y cũng cần được nhấn mạnh, rằng sự thiếu s&oacute;t của VCS kh&ocirc;ng phải l&agrave; yếu tố con người hay kỹ năng, m&agrave; l&agrave; cơ hội v&agrave; sự ổn định t&agrave;i ch&iacute;nh - Điều sẽ ho&agrave;n to&agrave;n được lưu t&acirc;m nếu một v&agrave;i người chơi chuyển ra nước ngo&agrave;i. Vấn đề n&agrave;y cũng đ&atilde; được nhắc lại trong một cuộc tr&ograve; chuyện kh&aacute;c, giữa đại diện của Riot Games v&agrave; một huyền thoại của VCS - Đỗ &quot;Levi&quot; Duy Kh&aacute;nh. Sau thời gian thi đấu tại Bắc Mỹ, việc anh ấy trở về Việt Nam c&oacute; một mối li&ecirc;n hệ đến sự ph&aacute;t triển cũng như những thiếu s&oacute;t của khu vực n&agrave;y.</p>\r\n\r\n<p><em>&quot;Ch&uacute;ng t&ocirc;i c&oacute; nhiều t&agrave;i năng trẻ v&agrave; họ tự tin hơn khi thi đấu ở khu vực nội địa, nhưng mức lương trung b&igrave;nh của c&aacute;c tuyển thủ chuy&ecirc;n nghiệp vẫn thực sự thấp so với c&aacute;c khu vực kh&aacute;c, thậm ch&iacute; thấp hơn cả một số khu vực wild-card&quot;,&nbsp;</em>Levi chia sẻ.<em>&nbsp;&quot;Những g&igrave; ch&uacute;ng t&ocirc;i cần l&agrave;m cho n&oacute; (mức lương) trở n&ecirc;n cao hơn v&agrave;o tương lai n&agrave;o đ&oacute;, để c&aacute;c tuyển thủ thực sự c&oacute; động lực v&agrave; nỗ lực giữ lấy vị thế của họ. V&agrave; ch&uacute;ng t&ocirc;i sẽ c&oacute; thể t&igrave;m thấy nhiều t&agrave;i năng trẻ hơn.&quot;</em></p>\r\n\r\n<p>Quan điểm n&agrave;y đ&atilde; chứng minh sự cần thiết của việc xuất hiện một người chơi kiểu mẫu như SofM hoặc Levi, v&agrave; đ&oacute; l&agrave; l&yacute; do tại sao SofM trở th&agrave;nh c&aacute;i t&ecirc;n cực kỳ quan trọng v&agrave; sẽ ảnh hưởng đến tương lai của VCS, đặc biệt l&agrave; trong bối cảnh VCS kh&ocirc;ng thể tham dự CKTG năm nay.</p>\r\n', 'photo-1-160397150385615172064991604051680.jpeg', 1);

