INSERT INTO `tbl_product` (`id_product`, `title_product`, `price_product`, `desc_product`, `quantity_product`, `image_product`, `id_category_product`, `product_hot`) VALUES
(78, 'GK – 550CN038', '495000', '<p>Chịu tr&aacute;ch nhiệm sản phẩm: C&ocirc;ng Ty TNHH Dịch vụ v&agrave; Thương mại Anna Việt Nam</p>\r\n\r\n<p>Cảnh b&aacute;o: Bảo quản trong hộp k&iacute;nh</p>\r\n\r\n<p>Hướng dẫn sử dụng:</p>\r\n\r\n<p>+ Th&aacute;o k&iacute;nh bằng 2 tay</p>\r\n\r\n<p>+ Kh&ocirc;ng bỏ k&iacute;nh v&agrave;o cốp xe hoặc những nơi c&oacute; nhiệt độ cao l&agrave;m biến dạng k&iacute;nh.</p>\r\n\r\n<p>+ Kh&ocirc;ng bỏ k&iacute;nh v&agrave;o t&uacute;i s&aacute;ch nếu kh&ocirc;ng c&oacute; hộp k&iacute;nh, vật dụng nhọn như ch&igrave;a kh&oacute;a sẽ l&agrave;m xước k&iacute;nh.</p>\r\n\r\n<p>+ Kh&ocirc;ng rửa k&iacute;nh lau k&iacute;nh bằng c&aacute;c chất c&oacute; t&iacute;nh tẩy rửa mạnh l&agrave;m bong tr&oacute;c lớp v&aacute;ng phủ</p>\r\n', 50, 'TU-16611700236486.png', 32, 0),
